YUI.add("gallery-y-common-radios",function(e,t){e.CommonRadios=e.Base.create("gallery-y-common-radios",e.Base,[],{initializer:function(){var t=this;e.all(this.get("radioFieldSelector")).each(function(e,n){t.bindRadio(e)})},bindRadio:function(t){var n=this;t.all(this.get("radioInputSelector")).each(function(t){var r=t.get("parentNode");if(!r.hasClass(n.get("radioSelector"))){r.addClass(n.get("radioSelector"));var i=t.get("checked")?" checked":"";r.prepend(e.Lang.sub(n.get("maskHtml"),{checked:i}))}}),t.delegate("click",function(e){t.all(n.get("maskSelector")).removeClass("checked");var r=e.currentTarget.get("parentNode");r.one(n.get("maskSelector")).addClass("checked")},this.get("radioInputSelector")),this.on("refresh",function(){t.all("input").each(function(e){var t=e.get("checked"),r=e.get("parentNode");t?r.one(n.get("maskSelector")).addClass("checked"):r.one(n.get("maskSelector")).removeClass("checked")})})}},{ATTRS:{radioFieldSelector:{value:".custom-radio-buttons"},maskSelector:{value:".radio-mask"},radioSelector:{value:"radio"},radioInputSelector:{value:'input[type="radio"]'},maskHtml:{value:'<span class="radio-mask {checked}"></span>'}}})},"@VERSION@",{requires:["yui-base","base-build"]});
